public final static int compare(BaseDanmaku obj1, BaseDanmaku obj2) {

        if (obj1 == obj2) {
            return 0;
        }

        if (obj1 == null) {
            return -1;
        }

        if (obj2 == null) {
            return 1;
        }

        long val = obj1.getTime() - obj2.getTime();
        if (val > 0) {
            return 1;
        } else if (val < 0) {
            return -1;
        }
        int r = obj1.index - obj2.index;
        if (r != 0)
            return r < 0 ? -1 : 1;

        r = obj1.hashCode() - obj1.hashCode();
        return r;
    }

